import os
import json
import sqlite3
from pathlib import Path
from datetime import datetime, timedelta
import subprocess

HOME = Path.home()
CUTOFF_TIME = datetime(2026, 2, 6, 22, 0, 0)  # 10pm tonight

def get_mtime(path: Path) -> datetime:
    """Get modification time of a file."""
    try:
        return datetime.fromtimestamp(path.stat().st_mtime)
    except:
        return datetime.min

def find_recent_files(root: Path, cutoff: datetime, extensions: list = None, max_depth: int = 5) -> list:
    """Find files modified after cutoff time."""
    results = []

    def walk_with_depth(path: Path, depth: int):
        if depth > max_depth:
            return
        try:
            for item in path.iterdir():
                if item.is_file():
                    if extensions is None or item.suffix in extensions:
                        mtime = get_mtime(item)
                        if mtime > cutoff:
                            results.append((item, mtime))
                elif item.is_dir() and not item.name.startswith('.git'):
                    walk_with_depth(item, depth + 1)
